import { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import api from '../axiosConfig';
import Header from '../components/Header';
import Footer from '../components/Footer';
import UserInfo from '../components/UserInfo';
import ProductCard from '../components/ProductCard';
import { toast } from 'react-toastify';

function DashboardPage() {
  const { user } = useContext(AuthContext);
  const [userInfo, setUserInfo] = useState({});
  const [recommendations, setRecommendations] = useState([]);
  const navigate = useNavigate();

  // Dữ liệu tĩnh (mô phỏng, không cần database)
  const [accountData] = useState({
    balance: 12500.75,
    activeLoans: 2,
    status: 'Good Standing',
  });
  const [loans] = useState([
    {
      id: 1,
      amount: 15000,
      apr: 7.99,
      monthlyPayment: 475.22,
      nextDueDate: '2025-06-15',
      remainingBalance: 10250.30,
    },
    {
      id: 2,
      amount: 8000,
      apr: 12.99,
      monthlyPayment: 286.45,
      nextDueDate: '2025-06-10',
      remainingBalance: 4500.10,
    },
  ]);
  const [recentActivity] = useState([
    { id: 1, date: '2025-05-01', type: 'Loan Payment', amount: -475.22, status: 'Completed' },
    { id: 2, date: '2025-04-28', type: 'Loan Application', amount: 8000, status: 'Approved' },
    { id: 3, date: '2025-04-15', type: 'Loan Payment', amount: -286.45, status: 'Completed' },
  ]);
  const [paymentReminders] = useState([
    { id: 1, loanId: 1, dueDate: '2025-06-15', amount: 475.22 },
    { id: 2, loanId: 2, dueDate: '2025-06-10', amount: 286.45 },
  ]);
  const [creditScore] = useState({
    score: 720,
    status: 'Excellent',
    lastUpdated: '2025-05-01',
  });

  useEffect(() => {
    console.log('Dashboard user:', user);
    if (!user) {
      console.log('No user, redirecting to login');
      navigate('/login');
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await api.get(`/api/user/${user.username}`);
        console.log('User info response:', response.data);
        setUserInfo(response.data);
      } catch (error) {
        console.error('Failed to fetch user info:', error.response || error.message);
        toast.error('Error loading user info: ' + (error.response?.data || error.message));
        if (error.response?.status === 401) {
          console.log('Unauthorized, redirecting to login');
          navigate('/login');
        }
      }
    };

    const fetchRecommendations = async () => {
      try {
        const response = await api.get(`/api/recommend/${user.username}`);
        console.log('Recommendations:', response.data);
        setRecommendations(response.data.slice(0, 4));
      } catch (error) {
        console.error('Error fetching recommendations:', error);
        toast.error('Error loading recommendations: ' + (error.response?.data || error.message));
      }
    };

    fetchUserInfo();
    fetchRecommendations();
  }, [user, navigate]);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <Header />
      <div className="flex-grow">
        {/* Welcome Section */}
        <section className="bg-blue-600 text-white py-16">
          <div className="container mx-auto px-6 text-center">
            <h1 className="text-4xl font-bold mb-4">Welcome, {user?.username || 'User'}!</h1>
            <p className="text-lg max-w-2xl mx-auto">
              Manage your loans, explore personalized recommendations, and take control of your finances.
            </p>
          </div>
        </section>

        {/* Main Content */}
        <div className="container mx-auto px-6 py-12">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Left Column: User Info, Loan Details, Payment Reminders */}
            <div className="lg:col-span-2 space-y-8">
              {/* User Info */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Your Profile</h2>
                <UserInfo userInfo={userInfo} />
              </section>

              {/* Loan Details */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Your Loans</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {loans.map((loan) => (
                    <div key={loan.id} className="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-shadow">
                      <h3 className="text-xl font-semibold text-gray-800 mb-4">Loan #{loan.id}</h3>
                      <p className="text-gray-600 mb-2"><strong>Loan Amount:</strong> ${loan.amount.toFixed(2)}</p>
                      <p className="text-gray-600 mb-2"><strong>APR:</strong> {loan.apr}%</p>
                      <p className="text-gray-600 mb-2"><strong>Monthly Payment:</strong> ${loan.monthlyPayment.toFixed(2)}</p>
                      <p className="text-gray-600 mb-2"><strong>Next Due Date:</strong> {loan.nextDueDate}</p>
                      <p className="text-gray-600 mb-4"><strong>Remaining Balance:</strong> ${loan.remainingBalance.toFixed(2)}</p>
                      <Link
                        to={`/loan/${loan.id}`}
                        className="inline-block bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                        onClick={(e) => e.preventDefault()} // Placeholder
                      >
                        View Details
                      </Link>
                    </div>
                  ))}
                </div>
              </section>

              {/* Payment Reminders */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Payment Reminders</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  {paymentReminders.length > 0 ? (
                    <ul className="space-y-4">
                      {paymentReminders.map((reminder) => (
                        <li key={reminder.id} className="flex justify-between items-center border-b pb-2">
                          <div>
                            <p className="text-gray-800 font-semibold">Loan #{reminder.loanId}</p>
                            <p className="text-gray-600">Due: {reminder.dueDate}</p>
                          </div>
                          <p className="text-blue-600 font-bold">${reminder.amount.toFixed(2)}</p>
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p className="text-gray-600">No upcoming payments.</p>
                  )}
                </div>
              </section>
            </div>

            {/* Right Column: Account Overview, Recommendations, Recent Activity, Credit Score, Quick Actions */}
            <div className="space-y-8">
              {/* Account Overview */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Account Overview</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg space-y-4">
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">Account Balance</h3>
                    <p className="text-2xl font-bold text-blue-600">${accountData.balance.toFixed(2)}</p>
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">Active Loans</h3>
                    <p className="text-2xl font-bold text-blue-600">{accountData.activeLoans}</p>
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">Account Status</h3>
                    <p className="text-2xl font-bold text-blue-600">{accountData.status}</p>
                  </div>
                </div>
              </section>

              {/* Recommendations */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Recommended for You</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  {recommendations.length > 0 ? (
                    <div className="space-y-4">
                      {recommendations.map((product) => (
                        <ProductCard key={product.productId} product={product} />
                      ))}
                    </div>
                  ) : (
                    <div className="text-center">
                      <p className="text-gray-600 mb-4">No recommendations available.</p>
                      <Link
                        to="/products/personal-loan"
                        className="inline-block bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                      >
                        Explore Loans
                      </Link>
                    </div>
                  )}
                </div>
              </section>

              {/* Recent Activity */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Recent Activity</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-blue-600 text-white">
                        <th className="py-2 px-4 text-left text-sm">Date</th>
                        <th className="py-2 px-4 text-left text-sm">Type</th>
                        <th className="py-2 px-4 text-left text-sm">Amount</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentActivity.map((activity) => (
                        <tr key={activity.id} className="border-b">
                          <td className="py-2 px-4 text-gray-600 text-sm">{activity.date}</td>
                          <td className="py-2 px-4 text-gray-600 text-sm">{activity.type}</td>
                          <td className="py-2 px-4 text-gray-600 text-sm">
                            {activity.amount < 0 ? '-' : ''}${Math.abs(activity.amount).toFixed(2)}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </section>

              {/* Credit Score Snapshot */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Credit Score</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  <p className="text-2xl font-bold text-blue-600 mb-2">{creditScore.score}</p>
                  <p className="text-gray-600 mb-2"><strong>Status:</strong> {creditScore.status}</p>
                  <p className="text-gray-600 text-sm"><strong>Last Updated:</strong> {creditScore.lastUpdated}</p>
                  <Link
                    to="/credit-tips"
                    className="inline-block mt-4 text-blue-500 hover:text-blue-600 text-sm"
                    onClick={(e) => e.preventDefault()} // Placeholder
                  >
                    Tips to Improve Your Score
                  </Link>
                </div>
              </section>

              {/* Quick Actions */}
              <section>
                <h2 className="text-3xl font-bold mb-6">Quick Actions</h2>
                <div className="bg-white p-6 rounded-lg shadow-lg space-y-4">
                  <Link
                    to="/products/personal-loan"
                    className="block bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors text-center"
                  >
                    Apply for a New Loan
                  </Link>
                  <Link
                    to="/contact"
                    className="block bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors text-center"
                    onClick={(e) => e.preventDefault()} // Placeholder
                  >
                    Contact Support
                  </Link>
                </div>
              </section>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default DashboardPage;
